import { Component, ElementRef, OnInit, ViewChild } from '@angular/core';
import { USGSOverlay } from './USGSOverlay';

/**
 * google map component
 */
@Component({
  selector: 'app-google-map',
  templateUrl: './google-map.component.html',
  styleUrls: ['./google-map.component.scss']
})
export class GoogleMapComponent implements OnInit {
  @ViewChild('gMap', { static: true }) gMap?: ElementRef;
  private map?: google.maps.Map;
  private image = 'data:image/png;base64,';
  private imageBounds: google.maps.LatLngBoundsLiteral = {
    east: -94.93816375732422,
    north: 43.78601837158203,
    south: 43.77880859375,
    west: -94.94806671142578
  };

  private polygonPath: google.maps.LatLngLiteral[] = [
    { lat: 43.7855579836773, lng: -94.9393847380537 },
    { lat: 43.7857661958145, lng: -94.9385096522015 },
    { lat: 43.7856415485432, lng: -94.9385021188018 },
    { lat: 43.7855699820927, lng: -94.9386755028832 },
    { lat: 43.7840427382476, lng: -94.9454749798974 },
    { lat: 43.7839973665658, lng: -94.9479675420163 },
    { lat: 43.7788870219455, lng: -94.948000611687 },
    { lat: 43.778822803413, lng: -94.9383143916803 },
    { lat: 43.780630558576, lng: -94.9382305846974 },
    { lat: 43.7811123034682, lng: -94.9384539065568 },
    { lat: 43.7812831077762, lng: -94.938209092948 },
    { lat: 43.7856654417829, lng: -94.9381691576296 },
    { lat: 43.7857456718989, lng: -94.9382210363634 },
    { lat: 43.7856561197246, lng: -94.9384727015282 },
    { lat: 43.7859069556794, lng: -94.9384743286711 },
    { lat: 43.7860034262286, lng: -94.9478731607036 },
    { lat: 43.7841237796033, lng: -94.947979323346 },
    { lat: 43.7840951289078, lng: -94.9456516119215 }
  ];

  ngOnInit(): void {
    this.map = new google.maps.Map(this.gMap.nativeElement, {
      zoom: 15,
      center: { lat: 24.886, lng: -70.268 }
    });

    const bounds = new google.maps.LatLngBounds(
      new google.maps.LatLng(this.imageBounds.south, this.imageBounds.west),
      new google.maps.LatLng(this.imageBounds.north, this.imageBounds.east)
    );

    // Construct the polygon.
    const bermudaTriangle = new google.maps.Polygon({
      paths: this.polygonPath,
      strokeColor: '#FF0000',
      // strokeOpacity: 0.8,
      strokeWeight: 3,
      fillColor: '#FF0000',
      // fillOpacity: 0.35
    });
    bermudaTriangle.setMap(this.map);

    this.map.panTo({ lat: 43.7824, lng: -94.9431 });

    const overlay = new USGSOverlay(bounds, this.image);
    overlay.setMap(this.map);
    this.map.fitBounds(this.imageBounds);
  }
}
